    private void btnEliminarTodoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEliminarTodoActionPerformed
    
        try {
            int valor = JOptionPane.showConfirmDialog(this, "Â¿Desea borrar de forma permanente las tareas de los alumnos?", "Advertencia",
                    JOptionPane.YES_NO_OPTION,JOptionPane.WARNING_MESSAGE);
            
            if(valor == JOptionPane.YES_OPTION){
                var ps = cn.prepareStatement("TRUNCATE TABLE notas");
                
                ps.execute();
               
                
                JOptionPane.showMessageDialog(null, "Ha eliminado todas las tareas de forma permanente","",JOptionPane.INFORMATION_MESSAGE);
                
                
            }
        } catch (SQLException e) {
            System.err.println(e);
            JOptionPane.showMessageDialog(null, "Error al eliminar todas las tareas");
        }
        
        
    }//GEN-LAST:event_btnEliminarTodoActionPerformed

